conditional_main_server.with_frontend:
import express from 'express';
import cors from 'cors';
import path from 'path';
import { logger } from './_shared/local/logger';

const app = express();
app.use(cors());
app.use(express.json());

// Serve React frontend
app.use(express.static(path.join(__dirname, 'ui/dist')));

// API routes
app.use('/api/v1/{module-name}', {MODULE}Routes);

// Frontend fallback
app.get('*', (req, res) => {
  res.sendFile(path.join(__dirname, 'ui/dist/index.html'));
});

conditional_main_server.without_frontend:
import express from 'express';
import cors from 'cors';
import { logger } from './_shared/local/logger';

const app = express();
app.use(cors());
app.use(express.json());

// API routes only
app.use('/api/v1/{module-name}', {MODULE}Routes);

// Health check
app.get('/health', (req, res) => {
  res.json({ status: 'healthy', mode: process.env.USE_MOCK_DB === 'true' ? 'mock' : 'production' });
});
